package com.chemaxon.bzaicsek.challenge

import groovy.transform.Canonical

class BathRoom {

    Pos pos = new Pos(1,1)

    BathRoom() {
        instructions.split('\n').each {move(it) }
    }

    def move(String line) {
        for(int i=0; i<line.length(); ++i) {
            switch(line.charAt(i)) {
                case 'U': move(0, -1); break;
                case 'R': move(1, 0); break;
                case 'D': move(0, 1); break;
                case 'L': move(-1, 0); break;
                default: throw new IllegalStateException("what is '${line.charAt(i)}'?")
            }
        }
        System.out.print(PAD[pos])
    }

    def move(int xDif, int yDif) {
        if(pos.x+xDif in minX..maxX) {
            pos.x += xDif
        }
        if(pos.y+yDif in minY..maxY) {
            pos.y += yDif
        }
    }

    @Canonical
    static class Pos {
        int x = 0
        int y = 0
    }


    static final int minX = 0
    static final int maxX = 2
    static final int minY = 0
    static final int maxY = 2

    static final Map<Pos, Integer> PAD = [
            new Pos(0,0):1, new Pos(1,0):2, new Pos(2,0):3,
            new Pos(0,1):4, new Pos(1,1):5, new Pos(2,2):6,
            new Pos(0,2):7, new Pos(1,2):8, new Pos(2,2):9]

    static String instructions = '''



'''

    static void main(String[] args) {
        new BathRoom()
        println()
    }
}
