package com.chemaxon.bzaicsek.challenge

import groovy.transform.Canonical

class ComplicatedBathRoom {

    Pos pos = new Pos(0,2)

    ComplicatedBathRoom() {
        instructions.split('\n').each {move(it) }
    }

    def move(String line) {
        for(int i=0; i<line.length(); ++i) {
            switch(line.charAt(i)) {
                case 'U': move(0, -1); break;
                case 'R': move(1, 0); break;
                case 'D': move(0, 1); break;
                case 'L': move(-1, 0); break;
                default: throw new IllegalStateException("what is '${line.charAt(i)}'?")
            }
        }
        System.out.print(PAD[pos])
    }

    def move(int xDif, int yDif) {
        if(PAD.containsKey(new Pos(pos.x+xDif, pos.y+yDif))) {
            pos.x += xDif
            pos.y += yDif
        }
    }

    @Canonical
    static class Pos {
        int x = 0
        int y = 0
    }

    static final Map<Pos, Character> PAD = [
            new Pos(2,0):'1',
            new Pos(1,1):'2', new Pos(2,1):'3', new Pos(3,1):'4',
            new Pos(0,2):'5', new Pos(1,2):'6', new Pos(2,2):'7', new Pos(3,2):'8', new Pos(4,2):'9',
            new Pos(1,3):'A', new Pos(2,3):'B', new Pos(3,3):'C',
            new Pos(2,4):'D'
    ]

    static String instructions = '''



'''

    static void main(String[] args) {
        new ComplicatedBathRoom()
        println()
    }
}
